azureLogAnalytics.query:
let appName = '$AppName';
print appName
| extend AppRoleName = 
    iff(appName == 'connectorxl', 'Connector.XL', 
        iff(appName == 'connectorcore', 'ConnectorCore',
            iff(appName == 'livedatacore', 'LiveData.Core',
                iff(appName == 'command-and-control-web', 'Willow.CommandAndControl', 
                    iff(appName == 'edge-deployment-dashboard', 'Willow.IoTService.Deployment.Dashboard', 
                        iff(appName == 'mapped-telemetry-adaptor', 'Willow.MappedTelemetryAdaptor', 
                            iff(appName == 'iothub-telemetry-adaptor', 'Willow.LiveData.IoTHubAdaptor', 
                                iff(appName == 'telemetry-streaming', 'Willow.LiveData.TelemetryStreaming', 
                                    iff(appName == 'cognian-telemetry-adapter', 'Willow.CognianTelemetryAdapter', appName)
                                )
                            )
                        )
                    )
                )
            )
        )
    )
| project AppRoleName
